#include<stdio.h>
#include<string.h>
int main()
{
    char str[1000];
    int value=0,A=0,D=0,i;
    printf("Enter the string:");
    fgets(str,sizeof str,stdin);
    for(i=0;str[i]!='\0';i++)
    {
        if((str[i]>'a'&& str[i]<'z') || str[i]>'A'&& str[i]<'Z')
        A=1;
        if(str[i]>'0'&& str[i]<'9')
        D=1;
    }
    if(A==1 && D==1)
    {
        printf("%s is alphanumric",str);
    }
    else 
    {
        printf("%s is not alphanumric ",str);
    }
    
   
    
    return 0;
}
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            